import React, { Component } from 'react';
import {fetchBrazilianDataByUf} from '../../global/services/covid-19';

import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

class Estados extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            brazilianData:[]
         }
    }

    async componentDidMount() {
        console.log(await fetchBrazilianDataByUf())
        this.setState({
            brazilianData: await fetchBrazilianDataByUf()
        });
    }

    render() { 
        let cols = [
            {}
        ]

        return ( 
            <div className="p-grid p-fluid">
                <div className="p-col-12 p-lg-12">                
                    <DataTable value={this.state.brazilianData}  >
                        <Column field="provinceState" header="Estado" sortable={true}/>
                        <Column field={"confirmed" + "ZZZ"} header="Confirmados" sortable={true}/>
                        <Column field="recovered" header="Recuperados" sortable={true}/>
                        <Column field="deaths" header="Mortes" sortable={true}/>
                    </DataTable>
                </div>
            </div> 
        );
    }
}

export default Estados;
